import {createApp} from 'vue'
import App from './App.vue'
import createRouter from './createRouter'
import createStore from "./createStore";

export default function () {
  // 创建 router 实例
  const router = createRouter()
  const store = createStore()

  console.log(55, router);

  const app = createApp(App)

  app.use(router)
  app.use(store)

  // 返回 app 和 router
  return { app, router }
}

import {createRouter, createWebHistory, createWebHashHistory} from 'vue-router'


// TODO 代码分割支持的原理(router?)
const routes = [
  {path: '/', component: () => import('./pages/Home.vue')},
  {path: '/about', component: () => import('./pages/AboutUs.vue')},
]

// NOTE: vue-router可以运行在服务端
// 服务端避免单例
export default function () {

  const router = createRouter({
    history: createWebHashHistory(),
    routes,
  })

  router.beforeEach((to, from) => {
    // ...
    // 返回 false 以取消导航
    console.log(to)
  })


  return router

}

import Vuex from "vuex"

export default function createStore() {
    return new Vuex.Store({
        state: {
            count: 0,
            user: {}
        },
        mutations: {
            setCount(state, value) {
                state.count = value
            },
            setUser(state, value) {
                state.user = value
            }
        },
        actions: {
            fetchUser({commit}) {
                // 接口请求数据
                return fetchUserInfo().then(data => {
                    commit('setUser', data)
                })
            }
        }
    })
}

function fetchUserInfo() {
    return new Promise(resolve => {
        setTimeout(() => {
            resolve({name: 'cc', id: 123})
        }, 1000)
    })
}

const createApp = require('./createApp')

// 避免单例：
// - 返回一个创建vue实例的工厂函数：单个vue实例，在被多个用户请求重复使用，造成共享很糟糕，比如登录状态
// - router
// - vuex
// 避免副作用
function createServerApp(context) {

    const {app, router, store} = createApp()

    // 因为路由导致异步，加个promise
    return new Promise((resolve, reject) => {
        // 在服务端设置router，注意这里需要保障server路由和router一致
        router.push(context.url)
        // 等到 router 将可能的异步组件和钩子函数解析完
        router.onReady(() => {
            const matchedComponents = router.getMatchedComponents()
            if (!matchedComponents.length) {
                return reject({ code: 404 })
            }
            // 页面需要的数据 -> ssr需要
            Promise.all(matchedComponents.map(cpn => {
                if (!cpn.asynData) {
                    return
                }
                return cpn.asynData({store, route: router.currentRoute})
            })).then(() => {
                // 当使用 template 时，context.state 将作为 window.__INITIAL_STATE__ 状态，自动嵌入到最终的 HTML 中。
                context.state = store.state
                // 完成app创建
                resolve(app)
            })
        }, reject)
    })

}

module.exports = createServerApp

